mapper.select:
select *
from chattingroom
where request_user = #{id}
or response_user = #{id}

select *
from chatmessage
where id = #{id}
order by num DESC LIMIT 15;

select *
from member
where mbti = #{mbti}
and gender = #{gender}
and user_id != #{user_id}
order by RAND() LIMIT 1;

select *
from member
where gender = #{gender}
and user_id != #{user_id}
order by RAND() LIMIT 1;

select id
from chattingroom
where request_user = #{user_id}
order by id DESC LIMIT 1;

select *
from chattingroom
where request_user = #{request_user}
and response_user = #{response_user}
